    private void sumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sumActionPerformed
        // TODO add your handling code here:
        double num1,num2,resultado;
num1=Double.parseDouble(Num1.getText());
num2=Double.parseDouble(Num2.getText());
resultado= num1 +num2;
Resultado.setText(""+resultado);
    }//GEN-LAST:event_sumActionPerformed

    private void restrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_restrActionPerformed
        // TODO add your handling code here:
        double num1,num2,resultado;
num1=Double.parseDouble(Num1.getText());
num2=Double.parseDouble(Num2.getText());
resultado= num1 -num2;
Resultado.setText(""+resultado);
    }//GEN-LAST:event_restrActionPerformed

    private void multActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_multActionPerformed
        // TODO add your handling code here:
        double num1,num2,resultado;
num1=Double.parseDouble(Num1.getText());
num2=Double.parseDouble(Num2.getText());
resultado= num1 * num2;
Resultado.setText(""+resultado);
    }//GEN-LAST:event_multActionPerformed

    private void divirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divirActionPerformed
        // TODO add your handling code here:
        double num1,num2,resultado;
num1=Double.parseDouble(Num1.getText());
num2=Double.parseDouble(Num2.getText());
resultado= num1 /num2;
Resultado.setText(""+resultado);
    }//GEN-LAST:event_divirActionPerformed

